import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import { TextField } from "@material-ui/core";

const useStyles = makeStyles({
  root: {
    minWidth: 275,
  },
  bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)",
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
  box: { backgroundColor: "green", borderRadius: "5%" },
});

export default function Homework() {
  const classes = useStyles();
  const bull = <span className={classes.bullet}>•</span>;

  return (
    <Card className={classes.root} variant="outlined">
      <CardContent>
        <Typography
          className={classes.title}
          color="textSecondary"
          gutterBottom
        >
          {" "}
          Bài 26
        </Typography>

        <Typography variant="h5" component="h2">
          Tên bài{" "}
        </Typography>
        <Typography variant="body1" component="p">
          Bài tập 1 <br />
        </Typography>
        <Typography variant="body2" component="p">
          例. シャラー（の）お湯（が）出ません
        </Typography>
        <Typography variant="body2" component="p" className="row">
          １．9時半　 [____の_____] 新幹線 [_____に____] 間に合いませんでした。
        </Typography>
        <br />
        <Typography variant="body2" component="p" className="row">
          ２．学校　 [____に_____] おくれたこどがありませんか
        </Typography>
        <br />
        <Typography variant="body2" component="p" className="row">
          ３. エアコン　の　調子　が　悪いんでしが。どこ [______で___]
          連絡　したらいいでしか。。
        </Typography>
        <br />
        <Typography variant="body2" component="p" className="row">
          ４．ごみ　は　駐車場　 [_____の____]
          横　[_____の____]　ごみ　置き場　[____で_____]　でしてください。
        </Typography>
        <br />
        <Typography variant="body1" component="p">
          Bài tập ２ <br />
        </Typography>
        <Typography variant="body2" component="p" className={classes.box}>
          いいです、悪かったです、下手です、病気です、遅れました、故障しました、書きます、来ませんでした、休みじゃありません、食べません、しています。。
        </Typography>
        <br />
      </CardContent>
      <CardActions>
        <Button size="small">Learn More</Button>
      </CardActions>
    </Card>
  );
}
